import React, { useState } from 'react';
import './VideoPlayer.css';

function VideoPlayer({ videoUrl = '/sample-sonar.mp4', thumbnail = null }) {
  const [isPlaying, setIsPlaying] = useState(false);

  const handleOpen = () => {
    setIsPlaying(true);
  };

  const handleClose = () => {
    setIsPlaying(false);
  };

  return (
    <>
      {/* Thumbnail/Placeholder */}
      <div className="video-player-thumbnail" onClick={handleOpen}>
        {thumbnail ? (
          <img src={thumbnail} alt="Sonar preview" />
        ) : (
          <div className="video-placeholder">
            <div className="play-icon">▶</div>
            <p>Click to view sonar feed</p>
          </div>
        )}
      </div>

      {/* Video Modal */}
      {isPlaying && (
        <div className="video-modal-overlay" onClick={handleClose}>
          <div className="video-modal-content" onClick={(e) => e.stopPropagation()}>
            <button className="video-close-btn" onClick={handleClose}>✕</button>
            <video
              src={videoUrl}
              controls
              autoPlay
              className="sonar-video"
            >
              Your browser does not support video playback.
            </video>
          </div>
        </div>
      )}
    </>
  );
}

export default VideoPlayer;
